abstract contract AbstractReverseResolver is
    IExtendedResolver,
    INameReverser,
    ERC165
{
    /// @notice The coin type for the resolver.
    uint256 public immutable coinType;

    /// @notice The address returned by `addr(coinType)` for the resolver.
