                            if (!ok || v.length == 0) {
                                lu.flags |= FLAG_CALL_ERROR;
                            }
                            if (v.length == 0) {
                                lu.flags |= FLAG_EMPTY_RESPONSE;
                            }
                        }
                    }
                    lu.data = v;
                }
                ++expected;
            }
        }
        if (expected != responses.length) {
            revert InvalidBatchGatewayResponse();
        }
        _revertBatchGateway(batch);
    }
}
