    /// @dev Error selector: `0x01800152`
    error HttpError(uint16 status, string message);

    /// @dev Information extracted from an `OffchainLookup` revert.
    struct Request {
        address sender;
        string[] urls;
        bytes data;
    }

    /// @notice Perform multiple `OffchainLookup` in parallel.
